templateFiles.main.tf:
# Terraform configuration for {{ .Environment }}

resource "aws_instance" "web" {
  count = {{ .InstanceCount }}
  ami           = "ami-12345678"
  instance_type = "t2.micro"

  tags = {
    Name = "web-{{ .Environment }}-${count.index}"
    Environment = "{{ .Environment }}"
  }
}
